import * as React from "react";
import ProductCard from "../../components/cards/productcard";
import { useNavigate } from 'react-router-dom';


export default function Inventory() {
  const products = [
    {
      name: "WHEY",
      price: "7000",
      image: "https://cdn.builder.io/api/v1/image/assets/TEMP/865d8cdadf0f49ed866ffb59d6cd058c3c0b4e6443808408fec6146ad325ec40?apiKey=dab1d767c7fa4022a44c79fa035746a8&",
    },
    {
      name: "Creatine",
      price: "8000", 
      image: "https://cdn.builder.io/api/v1/image/assets/TEMP/973bb59d13b827e89b8c891bb8f24241970a5b43d3ccb083c28c19a0c9ecc05a?apiKey=dab1d767c7fa4022a44c79fa035746a8&",
    },
  ];
  let navigate = useNavigate();

  const handleClick = () => {
    navigate('/inventory/add');
  };
  return (
    <>
      <div className="container">
        <button className="title"  onClick={handleClick}>Add Equipment</button>
        <div className="products">
          <div className="product-list">
            {products.map((product, index) => (
              <ProductCard key={index} product={product} />
            ))}
          </div>
        </div>
      </div>

      <style jsx>{`
        .container {
          display: flex;
          max-width: 572px;
          flex-direction: column;
          padding-top: 20px;
          padding-left: 80px; /* Add left padding */
          padding-right: 80px; /* Add right padding */
          overflow-x: hidden;
        }

        .title {
          border-radius: 100px;
          box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.25);
          background-color: #fff;
          align-self: start;
          color: #fecc09;
          justify-content: center;
          padding: 0 23px;
          font: 700 20px Poppins, sans-serif;
        }

        @media (max-width: 991px) {
          .title {
            padding: 0 20px;
          }
        }

        .products {
          align-self: center;
          margin-top: 17px;
          width: 100%;
          max-width: 540px;
        }

        @media (max-width: 991px) {
          .products {
            max-width: 100%;
          }
        }

        .product-list {
          gap: 20px;
          display: flex;
        }

        @media (max-width: 991px) {
          .product-list {
            flex-direction: column;
            align-items: stretch;
            gap: 0px;
          }
        }

        .product-card {
          border-radius: 30px;
          box-shadow: 0px 7px 10px 3px rgba(0, 0, 0, 0.25);
          background-color: #939393;
          display: flex;
          flex-grow: 1;
          flex-direction: column;
          font-weight: 700;
          width: 100%;
          padding: 14px 0;
        }

        @media (max-width: 991px) {
          .product-card {
            margin-top: 40px;
          }
        }

        .close-icon {
          aspect-ratio: 1;
          object-fit: auto;
          object-position: center;
          width: 17px;
          align-self: end;
          margin-left: -5px;
        }

        @media (max-width: 991px) {
          .close-icon {
            margin-right: 10px;
          }
        }

        .product-details {
          display: flex;
          flex-direction: column;
          padding: 0 8px 0 18px;
        }

        .product-image {
          aspect-ratio: 1.25;
          object-fit: auto;
          object-position: center;
          width: 174px;
          max-width: 100%;
          margin-left: 21px;
        }

        .product-info {
          display: flex;
          margin-top: 18px;
          gap: 20px;
          font-size: 17px;
          justify-content: space-between;
        }

        .product-name {
          color: #fecc09;
          font-family: Inter, sans-serif;
        }

        .product-price {
          color: #3e3e3e;
          font-family: Inter, sans-serif;
        }

        .order-button {
          border-radius: 30px;
          background-color: #fecc09;
          align-self: center;
          display: flex;
          margin-top: 21px;
          gap: 5px;
          font-size: 12px;
          color: #fff;
          padding: 5px 9px 5px 21px;
        }

        @media (max-width: 991px) {
          .order-button {
            padding-left: 20px;
          }
        }

        .order-text {
          font-family: Inter, sans-serif;
          flex-grow: 1;
          margin: auto 0;
        }

        .arrow-icon {
          aspect-ratio: 1.06;
          object-fit: auto;
          object-position: center;
          width: 16px;
        }
      `}</style>
    </>
  );
}
