import React from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import SideBar from './components/sidebar/sidebar';
import NavBar from './components/navbar/navbar';
import AllRoutes from './routes/routes';
import LoginPage from './pages/loginscreen/home';
import RegisterParticipant from './pages/registration/register';
import ErrorPage from './pages/error/error';
import './App.css';

function AppLayout({ children }) {
  return (
    <div className="App">
      <div className="sidebar">
        <SideBar />
      </div>
      <div className="content">
        <div className="navbar">
          <NavBar />
        </div>
        <div className="routes">
          {children}
        </div>
      </div>
    </div>
  );
}

function App() {
  const location = useLocation();

  // List of paths where sidebar and navbar should not be displayed
  const noNavAndSideBarPaths = ['/'];

  const isNoNavAndSideBar = noNavAndSideBarPaths.includes(location.pathname);

  return (
    <div className="App">
      {isNoNavAndSideBar ? (
        <Routes>
          <Route exact path="/" element={<LoginPage />} />
        </Routes>
      ) : (
        <AppLayout>
          <AllRoutes />
        </AppLayout>
      )}
    </div>
  );
}

export default App;
