import * as React from "react";
import CardOneContent from '../../components/cards/cardonecontent'
import CardTwoContent from '../../components/cards/cardtwocontent'


function Card({ children, style }) {
    return (
        <div style={style}>{children}
        <style jsx>{`
                .card {
                    border-radius: 12px;
                    box-shadow: 0px 4px 4px 0px rgba(0, 0, 0, 0.25);
                    background-color: #939393;
                    width: 100%;
                    margin-top: 20px; /* Add margin to the top */
                }
                `}</style>
        </div>
        

);
}







export default function Plan() {
    const cards = [
        { id: 1, minHeight: 348, content: <CardOneContent /> },
        { id: 2, minHeight: 386, content: <CardTwoContent /> },
    ];

    return (
        <>
            <main className="main-container">
            <h1 className="add-plan-title">ADD a Plan!</h1>
            {cards.map((card) => (
                <Card key={card.id} style={{ minHeight: `${card.minHeight}px` }}>
                    {card.content}
                </Card>
            ))}
            </main>

            <style jsx>{`
                .main-container {
                    display: flex;
                    max-width: 100%;
                    flex-direction: column;
                    padding-left: 80px; /* Add left padding */
                    padding-right: 80px; /* Add right padding */
                    overflow-x: hidden; /* Prevent horizontal scrolling */
                }
                .add-plan-title {
                    color: #deba3b;
                    letter-spacing: 1.9px;
                    max-width: 410px;
                    font: 700 38px Poppins, sans-serif;
                }
                .card-content {
                    min-height: 332px; /* Decrease the height */
                    width: 100%;
                }
                .card-2 {
                    margin-top: 36px;
                    min-height: 370px; /* Decrease the height */
                    border-radius: 20px;
                }
                .card + .card {
                    margin-top: 20px;
                }
                input[type="text"] {
                    border-radius: 12px;
                    border: 1px solid rgba(255, 255, 255, 1);
                    background-color: #fff;
                    min-height: 36px; /* Decrease the height */
                    max-width: 418px;
                    padding: 16px; /* Add padding */
                }
                .input-row {
                    display: flex;
                    align-items: center;
                    margin-bottom: 10px;
                }
                .input-label {
                    color: white;
                    font-weight: bold;
                    font-size: 18px;
                    margin-right: 10px;
                }

                @media (max-width: 991px) {
                    .main-container {
                        max-width: 100%; /* Make the container responsive */
                        padding-left: 20px; /* Adjust left padding on smaller screens */
                        padding-right: 20px; /* Adjust right padding on smaller screens */
                    }
                    .card {
                        max-width: 100%; /* Make the cards responsive */
                    }
                }
            `}</style>
        </>
    );
}
